css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 11pt;
	width: 100%;
	height: 100%;
	overflow: hidden;
}

.corner {
	font-size: 13pt;
	font-weight: 600;
	white-space:nowrap;
	transform: rotate(90deg) translateX(84%);
	transform-origin: 90% 90%;
}
.corner.reversed {
	transform: rotate(270deg) translateX(-8%);
	transform-origin: 0 0;
	border: 0
}

table {
	background-color: #eee;
	padding:3pt;
	border-radius: 2pt;
	border: 2px solid gray;	
}
th {text-align: right;}
td {text-align: left;}

.info {
	display: flex;
	flex: 1;
	justify-content: center;
	align-elements: center;
	margin-top: -12pt;
}

.main {
	display: flex;
	flex: 2;
	flex-direction: column;
	align-items: center;
	justify-content: flex-end;
	margin-bottom: -12pt;
}

img {
	max-height: 145pt;
	max-width: 125pt;
	border-radius: 2pt;
}
.cardImage img {
	max-height: 135pt;
}

#thing159143 img {
	border: 1px solid #bbb; /* for hue */
}
