css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 14pt;
	width: 100%;
	height: 100%;
}

.corner {
	display: flex;
	align-items: flex-start;
	flex-direction: row;
	font-weight: 600;
	padding: 5pt 7pt;
}
.players {
	color:tomato;
	text-align:center;
	width: 90%;
	font-size: 8pt;
	padding: 5pt;
}

.reversed {
	transform: rotate(180deg);}
.main {
	display: flex;
	align-items: center;
	justify-content: center;
	flex-direction: column;
	justify-content: space-between;
	font-family: 'Delius Swash Caps', sans-serif;
	font-size: 48pt;
  height:100%;
}

div.title {
	font-size:36pt;
	background-color: #aaaaaa;
}

div.color {
	display:flex;
	align-items: center;
	justify-content: center;
	height:75%;
	width: 75%;
	height: 100%; 
	border-radius: 5pt;
}

