css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 11pt;
	width: 100%;
	height: 100%;
	overflow: hidden;
}

.corner {
	display: flex;
	align-items: flex-start;
	justify-content: flex-start;
	flex-direction: column;
	font-size: 14pt;
	font-weight: 600;
	padding: 2pt 4pt;
}
.reversed {
	transform: rotate(180deg);
}
.main {
	display: flex;
	align-items: center;
	justify-content: flex-start;
	flex-direction: column;
	justify-content: space-between;
}

img {
	max-height: 120pt;
	min-height: 120pt;
	max-width: 225pt;
	border: 1px solid #bbb; /* for hue */
}
