css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 11pt;
	width: 100%;
	height: 100%;
	overflow: hidden;
}

.corner {
	font-size: 14pt;
	font-weight: 600;
	white-space:nowrap;
	transform: rotate(90deg) translateX(82%);
	transform-origin: 90% 90%;
}

.info {

}

.main {
	display: flex;
	flex: 1;
	flex-direction: column;
	align-items: center;
	justify-content: flex-end;
}

img {
	max-height: 120pt;
	max-width: 130pt;
	border-radius: 2pt;
}
#thing159143 img {
	border: 1px solid #bbb; /* for hue */
}
