css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 14pt;
	width: 100%;
	height: 100%;
}

.corner {
	display: flex;
	align-items: flex-start;
	flex-direction: row;
	font-weight: 600;
	padding: 5pt 7pt;
}
.reversed {
	transform: rotate(180deg);}
.main {
	display: flex;
	align-items: center;
	justify-content: flex-start;
	flex-direction: column;
	justify-content: space-between;
	font-family: 'Delius Swash Caps', sans-serif;
	font-size: 48pt;
}
