css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 14pt;
	width: 100%;
	height: 100%;
}

.corner {
	display: flex;
	align-items: center;
	justify-content: space-between;
	flex-direction: row;
	font-weight: 600;
	padding: 50pt 5pt 5pt 5pt;
}
.reversed {
	transform: rotate(180deg);
}

.dot {
	display: flex;
	flex-direction: row;
	justify-content:center;
	width: 50%;
	color:red;
	font-size: 24pt;
}

.dotMiddle {
	margin-top: -66pt;
}
.dotEdge {
	margin-top: -63pt;
}

.score1 .dotMiddle1,
.score1 .dotEdge {display:none;} 
.score2 .dotEdge {display:none;}  
.score3 .dotMiddle2 {display:none;}

.main {
	display: flex;
	align-items: center;
	justify-content: flex-start;
	flex-direction: column;
	justify-content: space-between;
	font-family: 'Delius Swash Caps', sans-serif;
	font-size: 48pt;
}
