.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: Raleway, sans-serif;
	font-size: 11pt;
	width: 100%;
	height: 100%;
	overflow: hidden;
}

.text.corner {
	font-size: 13pt;
	font-weight: 600;
	white-space:nowrap;
  	height:14pt;
  	width:200pt;
  	line-height:0.7;
  	font-family: Cochin, Didot, Garamond, serif;
}
.text.corner.straight {
	transform: rotate(90deg) translateX(84%) translateY(-50%);
	transform-origin: 90% 90%;
  	font-style:italic;
  	font-weight:400;
  	font-family: 'Hoefler Text', Zapfino, serif;
   	top:24pt;
	right:24pt;
}
.text.corner.reversed {
	transform: rotate(270deg) translateX(-7%) translateY(-7%);
	transform-origin: left top;
	font-style:normal;
  	bottom:24pt;
	left:24pt;
}
.rank.corner {
	font-size: 18pt;
	font-weight: 600;
	line-height:0.7;
}
.rank.corner.straight {
 	top:24pt;
	left:24pt;
}
.rank.corner.reversed {
 	bottom:24pt;
	right:24pt;
	transform: rotate(180deg);
  	transform-origin: center center;
}

.corner {
  position: absolute;
  text-shadow: 0 0 0.5em antiquewhite, 0 0 0.5em antiquewhite, 0 0 0.2em #DFC894;
}
.corner.yellow {
  text-shadow: 0 0 0.7em #17190C, 0 0 0.5em #17190C, 0 0 0.2em #40471B;
}
.corner.red {
   text-shadow: 0 0 0.5em antiquewhite, 0 0 0.5em antiquewhite, 0 0 0.5em antiquewhite, 0 0 0.2em #DFC894;
}
.corner.blue,
.corner.gray {
   text-shadow: 0 0 0.5em white, 0 0 0.5em white, 0 0 0.5em white, 0 0 0.2em #9DD;
}
