css:
body {
  font-family:Raleway,sans-serif;
  font-family: Cabin, sans-serif;
  font-size: 11pt;
  line-height:1.25;
  color: #654321;
}

h1, h2, h3, h4, h5, h6 {
  margin-top:5pt;
  text-align:center;
  color: #6B3820;
  font-family: Cochin, Didot, Garamond, serif; 
}

.card1 h1 {
 margin-top: 0; 
}

li span {
 	font-weight:bold; 
}

ol, ul {
  margin-left:-16pt; 
}

ul#species {
 list-style-type:none; 
 margin-left:0pt;
}

ul#species li {
 text-indent: -30pt;
  padding-bottom: 1pt;
}
ul#species li i {
 display:none; 
}

div.tableWrapper {
 	display:flex;
  	justify-content:center;
}

table {
  border-collapse: collapse;
}
th, td {
 	border: 1pt solid sienna;
  padding: 1pt 3pt;
}

th {
 text-align:right;
}

td {
 text-align: left; 
}

.brown {
 	color: #654321;
   	border-color: #654321!important;
}
.green {
 	color: darkgreen;
 	border-color: darkgreen!important; 
}
.black {
 	color: #000; 
}
.blue {
 	color: #0041C2; 
   	border-color: #0041C2!important; 
}
.red {
	color: #800000;
	border-color: #800000!important;
}
.orange {
 	color: #D47707; 
 	border-color: #D47707!important; 

}
.purple {
   	color: #520653;
   	border-color: #520653!important;
}
.yellow {
    color: #DFE730;
    border-color: #DFE730!important;
}
.pink {
 	color: #C5697E;
 	border-color: #C5697E!important;
}
.gray {
 	color: #555;
 	border-color: #555!important;
}
.transp {
	color: transparent;
	border-color: transparent!important;
}


p {
  text-align:justify;
}

div.weecard {
  display: inline-block;
  text-align: center;
  padding-top:4pt;
  font-size:12pt;
  height: 30pt;
  width: 20pt;
  border: 3pt solid transparent;
  border-radius: 3pt;
}
