css:
body {
  font-family:Raleway,sans-serif;
  font-family: Cabin, sans-serif;
  font-size: 14pt;
  line-height:1.25;
  color: #654321;
  background-color: #E6DFD1;
  overflow:hidden;
}

hr {
  	border-style: solid;
  	border-color: #654321;
  	border-radius: 50%;
	width:33%;
  	margin-top:-10pt;
    margin-bottom:-10pt;
  	padding:0;
}

img.titleImg {
 width: 150pt;
  z-index: 2;
}


img.rev {
	transform: rotate(180deg);
}

img.mainCol {
 position:absolute;
 left: 140pt;
}


img.mainCol.top {
 top: 51pt;
}
img.mainCol.top.rev {
 top: 120pt;
}

img.mainCol.front {
 top: 171pt;
}img.mainCol.front.rev {
 top: 371pt;
}

img.tree.front {
 width: 180pt;
  left: 126pt;
  top: 161pt;
  z-index:1;
}

img.mainCol.bot {
 top: 422pt;
}
img.mainCol.bot.rev {
 top: 491pt;
}

img.sideImg {
 margin-left: -100pt;
 margin-top: -30pt;
  height:800pt;
  opacity: 0.4;
}

img#comet {
 	width: 150pt;
  margin-top: 0pt;
  margin-left: -40pt;
  margin-bottom: -10pt;
}

h1, h2, h3, h4, h5, h6 {
  color: #6B3820;
  font-family: Cochin, Didot, Garamond, serif; 
}

h1 {
 	text-align:center; 
}
h1 em {
 	font-style:italic; 
}

div.blurb {
  text-align:center;
  font-size: 10pt;
  width: 160pt;
  position:absolute;
  top: 150pt;
  left: 416pt;
  background-color: hsla(40,70%,100%,0.6);
}
div.blurb p {
   padding: 10pt; 
}
