css:
.card {
	font-family: RennieMackintoshITC, Amarante, Cabin, sans-serif;
	font-size: 18pt;
	background-repeat: repeat;
}

.card1 {
	background-image: url(images/ssbacks_1.jpg);
	background-size: 100mm 100mm; 
	background-position: 20mm 34mm; 
}

.card2 {
	background-image: url(images/ssbacks_2.jpg);
	background-size: 100mm 100mm;
	background-position: 8mm 12mm;

}

.bleed {
	text-align:center;
	background-size: 80mm 80mm; 
	background-repeat: no-repeat;
	background-position: -17.5mm 16mm;
}

.card1 .bleed {
	background-image: url(images/ssbacks_glass1.svg);
}

.card2 .bleed {
	background-image: url(images/ssbacks_glass2.svg);
}

.subtitle {
	font-family: Cabin, sans-serif;
	font-size: 12pt;
}

.safe {
	display: flex;
	align-items: center;
	flex-direction: column;
	justify-content: center;
	flex: 1;
}
