css:
body {
  font-family:Raleway,sans-serif;
  font-size: 11pt;
}

h1, .sideTitle {
  font-family: RennieMackintoshITC, Amarante, Cabin, sans-serif;
  font-size: 26pt;
  margin:0;
  padding:0;
}

div.wrapper {
 	display:flex;
  	justify-content: center;
    align-items: center;
  	height:100%;
    flex-direction: column;
}

p {
	font-family: Cabin, sans-serif;
  	font-style: italic;
  text-align:center;
  margin:0;
  padding:0;
}

.card2 p {
 	padding-bottom: 12pt; 
}
.card1 {
  background-image: url(images/ssbacks_1.jpg);
  background-size: 100mm 100mm; 
  background-position: 20mm 34mm;
}
bleed {  
  background-image:  url(images/ssbacks_glass1.svg);
  background-repeat: no-repeat;
  background-size: 50% 50%;
  background-position: 55mm 72mm;
}
.card2 bleed {
	background-image: url(images/ssbacks_glass2.svg);
}

div.info {
  position:absolute;
  top:175mm;
  right:80mm;
}
div.info div img {
  border-radius: 2pt;
  height:18mm;
  margin-left:5pt;
}
div.sideTitle {
  position:absolute;
  top:111mm;
  left:109mm;
  transform: translate(90mm, 0mm) rotate(270deg);
  border: 2pt solid white;
  margin:0;
  padding:7pt 5pt 0 5pt;
  background-color: black;
  color:white;
  border-radius: 5pt;
}
