css:
safe {
	display: flex;
	justify-content: center;
}
card {
	font-family: Raleway, sans-serif;
	font-size: 14pt;
	background-image: url(images/ssclues_bg.svg);
	background-size: 80mm 80mm; 
	background-repeat: no-repeat;
	background-position: -17.5mm 20mm;
}
card.Drewry {
	color: blue;
	background-image: url(images/ssclues_bg_blue.svg);
}
card.Foss {
	color: green;
	background-image: url(images/ssclues_bg_green.svg);
}
card.Goldring {
	color: red;
	background-image: url(images/ssclues_bg_red.svg);
}
.info {
	display: flex;
	align-items: center;
	flex-direction: column;
	justify-content: center;
}
.Jigs.tempo {
	font-family: 'Fontdiner Swanky', cursive;
	font-size: 18pt;
}
.Strathspeys.tempo {
	font-family: Italianno, cursive;
	font-weight: bold;
	font-size: 28pt;
}
.Reels.tempo {
	font-family: 'Cherry Swash', cursive;
	font-weight: bold;
	font-size: 22pt;
}

.text {
	font-size: 16pt;
}
.devisor {
	font-family: Cabin, sans-serif;
	font-size: 16pt;
}
.count {
	font-size: 20pt;
	font-weight: 500;
}
.uncolored {
	color: black;
}
.special {
	font-family: Sriracha, cursive;
	font-size: 16pt;
	line-height: 0.8;
}
