css:
.safe {
	display: flex;
	flex-direction: column;
	justify-content: space-between;
}

.card {
	font-family: Raleway, sans-serif;
	font-size: 14pt;
}

.card.Drewry {
	color: blue;
}
.card.Foss {
	color: green;
}
.card.Goldring {
	color: red;
}

.corner {
	padding: 3pt;
}

.corner {
	display: flex;
	flex: 1;
	align-items: flex-start;
	flex-direction: row;
	font-weight: 600;
}

.info {
	display: flex;
	flex: ,;
	align-items: center;
	flex-direction: column;
	justify-content: flex-end;
}

.title {
	display:flex;
	flex: 1;
	align-items: flex-start;
	justify-content: center;
	font-family: Amiri, serif;
	font-style: italic;
	text-align:center;
}

.Jig.corner::before {
	content: 'J';
}
.Strathspey.corner::before {
	content: 'S';
}
.Reel.corner::before {
	content: 'R';
}

.Jig.tempo {
	font-family: 'Fontdiner Swanky', cursive;
	font-size: 18pt;
}

.Strathspey.tempo {
	font-family: Italianno, cursive;
	font-weight: bold;
	font-size: 28pt;
}

.Reel.tempo {
	font-family: 'Cherry Swash', cursive;
	font-weight: bold;
	font-size: 22pt;
}

.tempo {
	text-shadow: 4px 4px 4px #aaa;
}
.count, .devisor {
	text-shadow: 2px 2px 2px #ccc;
}

.Drewry.corner::after {
	content: 'D';
}
.Foss.corner::after {
	content: 'F';
}
.Goldring.corner::after {
	content: 'G';
}

.text {
	font-size: 16pt;
}

.devisor {
	font-family: Cabin, sans-serif;
	font-size: 16pt;
}

.Drewry.devisor::before {
	content: 'John ';
}

.Foss.devisor::before {
	content: 'Hugh ';
}

.Goldring.devisor::before {
	content: 'Roy ';
}

.count {
	font-size: 20pt;
	font-weight: 500;
}
.reversed {
	transform: rotate(180deg);}
