css:
body {
  font-family:Raleway,sans-serif;
  font-size: 10pt;
}

table {
  width:100%;
  border-collapse: collapse;
}

th {
  border: 2pt solid gray;
}

td {
  border: 1pt solid #bbb;
  text-align:center;
}

tr:nth-child(6) td,
tr:nth-child(7) td {
 border-bottom: 2pt solid gray; 
}

td:nth-of-type(4n),
td:nth-of-type(4n-1) {
  border-right: 2pt solid gray; 
}

td {
  height: 4em; 
}

tr.head2 th {
  width:6%
}

th.rotate > div {
  transform: 
    /* Magic Numbers */
    translate(0pt, 1pt)
    /* 45 is really 360 - 45 */
    rotate(270deg);
  	white-space:nowrap;
  width: 30px;
}

div.notes {
  padding:2pt 4pt;
}
