css:
body {
  font-family:Raleway,sans-serif;
  font-size: 10pt;
}

table {
  width:100%;
  border-collapse: collapse;
}

th {
  border: 2pt solid gray;
}

td {
  border: 1pt solid gray;
  text-align:center;
}

tr:nth-child(6) td,
tr:nth-child(7) td {
 border-bottom: 2pt solid gray; 
}

td:nth-of-type(4n),
td:nth-of-type(4n-1) {
  border-right: 2pt solid gray; 
}

td {
  height: 5em; 
}

.head1 th:nth-child(2),
.head2 th:nth-child(1),
.head2 th:nth-child(2),
.head2 th:nth-child(3),
.head2 th:nth-child(4),
td:nth-child(2),
td:nth-child(3),
td:nth-child(4),
td:nth-child(5) {
 	background-color:#C0CCFF; 
}
.head1 th:nth-child(3),
.head2 th:nth-child(5),
.head2 th:nth-child(6),
.head2 th:nth-child(7),
.head2 th:nth-child(8),
td:nth-child(6),
td:nth-child(7),
td:nth-child(8),
td:nth-child(9) {
 	background-color:#C0FFCB; 
}
.head1 th:nth-child(4),
.head2 th:nth-child(9),
.head2 th:nth-child(10),
.head2 th:nth-child(11),
.head2 th:nth-child(12),
td:nth-child(10),
td:nth-child(11),
td:nth-child(12),
td:nth-child(13) {
 	background-color:pink; 
}

tr.head2 th {
  width:6%
}

th.rotate > div {
  transform: 
    /* Magic Numbers */
    translate(0pt, 1pt)
    /* 45 is really 360 - 45 */
    rotate(270deg);
  white-space:nowrap;
  width: 30px;
}

div.notes {
  padding:2pt 4pt;
}

div.pad, div.rotatepad {
  height: 50%;
  border-top: 2pt dashed gray;
  padding-top: 6pt
}

div.rotatepad {
  transform: 
    /* Magic Numbers */
    translate(0pt, 1pt)
    /* 45 is really 360 - 45 */
    rotate(180deg);
}
