css:
body {
  font-family:Raleway,sans-serif;
  font-size: 10pt;
}

table {
  width:92%;
  height:92%;
  border-collapse: collapse;
  margin: 10pt 0 0 22pt;
}

th {
  border: 2pt solid gray;
  height: 5%;
}

td {
  border: 1pt solid #bbb;
  text-align:center;
}

tr:nth-child(6) td,
tr:nth-child(7) td {
 border-bottom: 2pt solid gray; 
}

td:nth-of-type(3n) {
  border-right: 2pt solid gray; 
}

td {
  height: 4em; 
}

tr.head2 th {
  width:11%
}

th.rotate > div {
  transform: 
    /* Magic Numbers */
    translate(0pt, 1pt)
    /* 45 is really 360 - 45 */
    rotate(270deg);
  	white-space:nowrap;
  width: 20pt;
}
