css:
.wrapper {
	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-between;
	font-family: "Kaushan Script", cursive;
	font-size: 10pt;
	width: 100%;
	height: 100%;
  	padding-left:2pt;
}

.head {
	font-size:12pt;
	width:100%;
	text-align:center;
}

.main {
  	display: flex;
	flex: 1;
	flex-direction: column;
	justify-content: space-around;
 	font-family: Raleway, sans-serif;
  	font-size: 10pt;
}

.main div {
	padding-left: 35pt;
	width:100%;
}

.rating {
 	display: inline-block;
  	color: gray;
  	padding-right:1pt;
}

img {
	height: 20pt;
	margin-left: -25pt;
  	padding-right: 5pt;
  	margin-bottom: -1pt;
    padding-bottom: 0;
}
